module.exports.HTMLHeader = '<html lang="en"><head><meta charset="UTF-8"><title>JUnit Test Results - junit-to-html</title><script src="https://ajax.googleapis.com/ajax/libs/jquery/2.2.4/jquery.min.js"></script><style>html{font-family:Arial,sans-serif,monospace}body{margin:0;display:flex;position:relative;}#content{display:flex;flex-direction:column;position:absolute;left:350px;}.suite,.suite-container{position:relative;display:flex;flex-direction:column;border:1px solid #000;padding:8px;margin:8px 16px}.suite .title{font-size:18px;display:block;padding:4px 2px;background:#4CAF50}.suite>.duration,[class^=count_]{font-size:14px}.has_error,.title.has_error{background:#F44336}.tests{border:1px solid #000;padding:8px;max-height:24px;overflow-y:hidden;transition:.3s ease max-height}.tests .expand_tests{display:block;padding:4px 2px;font-size:16px;font-weight:700;text-decoration:underline}.expand_tests::before{display:block;content:\'Expand Tests ▼\'}.tests.open{max-height:99999999px}.tests.open .expand_tests::before{content:\'Close Tests ▲\'}.tests .expand_tests:hover{background:#7EA6D7;cursor:pointer}.test{display:flex;flex-direction:column;border:1px solid #b6b6b6;margin:8px 0;padding:4px}.test .err_out{background:rgba(0,0,0,.2);padding:8px;margin:0 8px;border-radius:4px}nav{display:flex;flex-direction:column;position:fixed;left:0;height:100vh;max-width:350px;background:#222;overflow-y:auto;}#nav_counts{font-weight:700;color:#fff;text-align:center;}#nav_counts span{display:inline-block;padding:2px 4px;}nav a{color:#fff;text-decoration:none;position:relative;padding:4px;display:block;}nav a:hover{background:#7EA6D7}nav ul{list-style:none;padding:0;}nav li{display:block;position:relative;}nav li.suite{font-size:18px;font-weight:700;text-transform:uppercase;border-top:1px solid #b6b6b6;}nav li.sub{text-indent:6px;font-size:14px;background:#333;}nav li.test{text-indent:12px;border:none;font-size:12px;padding:0px;}nav li.test.error{background:rgba(255,0,0,.5)}</style></head><body><nav><ul>';

module.exports.HTMLFooter = `</div><!--Closes #content-->\n</body><script>$(function(){$('.expand_tests').on('click',function(){$(this).parent().toggleClass('open');});$('nav li.test a').on('click',function(){$($(this).attr('href')).parent().addClass('open');});});function openall(){var all = document.querySelectorAll('.tests');all.forEach(t =>{t.classList.toggle('open');})}</script></html>`;

